mapper.update:
update bci_team
set status = #{disband}
where id = #{teamId}

update bci_application
set status = #{status}
where team_id = #{teamId} and paradigm_id = #{paradigm}

mapper.select:
select bt.event_id, bt.id team_id, but.role, but.status
from bci_team bt join bci_user_team but on bt.id = but.team_id
where but.user_id = #{userId}

select paradigm_id, status
from bci_application
where team_id = #{teamId}

select team_name
from bci_team
where id = #{teamId}

select id, event_id, leader_id, team_name, instructor, university, status
from bci_team
where id = #{teamId}
