mapper.insert:
INSERT INTO bci_container_log (task_id, content)
VALUES (#{containerLogDO.taskId}, #{containerLogDO.content})
    ON DUPLICATE KEY UPDATE content = VALUES(content);

mapper.select:
select id, user_id, team_id, code_id, paradigm_id, task_type, task_name, dataset, container_id, status, create_time, score, compute_node_ip, show_status
from bci_task
where show_status = 1 and id = #{taskId}

select id, task_name, status, create_time
from bci_task
where show_status = 1 and user_id = #{userId} and paradigm_id = #{paradigm}
order by create_time desc
limit #{index}, #{pageSize}

select count(*)
from bci_task
where show_status = 1 and user_id = #{userId} and paradigm_id = #{paradigm}

select team_id, max(score) as score
from bci_task
where `paradigm_id` = #{paradigmId} and status = 2 and dataset = #{dataset} and show_status = 1 and score is not null and create_time < #{dateLine}
group by team_id
order by score desc, team_id asc
limit #{index}, #{pageSize}

select count(*)
from bci_task
where team_id = #{teamId} and `paradigm_id` = #{paradigmId} and status = 2 and dataset = #{dataset} and show_status = 1 and score is not null and create_time < #{dateLine}

select create_time, score
from bci_task
where team_id = #{teamId} and `paradigm_id` = #{paradigmId} and status = 2 and dataset = #{dataset} and show_status = 1 and score is not null and create_time < #{dateLine}
order by create_time desc
limit #{index}, #{pageSize}

select content
from bci_container_log
where task_id = #{taskId}
